//    @Column(name = "content_type_foto")
//    private String contentTypeFoto;
    @Column(name = "latch_accounid", length = 200)
    private String latchAccountid;
    @Column(name = "latch_token", length = 200)
    private String latchToken;
    @Column(name = "token_auth", length = 200)
    private String tokenAuth;

    /**
     * Setea en nulo los atributos del objeto. este método se invoca para anular
     * la sesión del usuario
     */
    public void resetear() {
        this.id = null;
        this.rol = null;
        this.documento = null;
        this.clave = null;
        this.nombres = null;
        this.apellidos = null;
        this.correoElectronico = null;
        this.telefonoOficina = null;
        this.telefonoOficina = null;
        this.llaveCambioClave = null;
        this.fechaInactivado = null;
//        this.foto = null;
//        this.contentTypeFoto = null;
        this.clave = null;
    }

    public String crearNombreCompleto() {
        return nombres + " " + apellidos;
    }

    public Usuarios() {
    }

    public Usuarios(Long id, Rol rol, String documento, String clave, String nombres, String apellidos, String correoElectronico) {
        this.id = id;
        this.rol = rol;
        this.documento = documento;
        this.clave = clave;
        this.nombres = nombres;
        this.apellidos = apellidos;
        this.correoElectronico = correoElectronico;
    }

