    /**
     * Copiar los datos de un objeto a otro
     * @param usuario 
     */
    public void copiarDatos(Usuarios usuario) {
        this.id = usuario.getId();
        this.rol = usuario.getRol();
        this.documento = usuario.getDocumento();
        this.clave = usuario.getClave();
        this.nombres = usuario.getNombres();
        this.apellidos = usuario.getApellidos();
        this.correoElectronico = usuario.getCorreoElectronico();
        this.telefonoOficina = usuario.getTelefonoOficina();
        this.telefonoCelular = usuario.getTelefonoCelular();
        this.llaveCambioClave = usuario.getLlaveCambioClave();
        this.fechaInactivado = usuario.getFechaInactivado();
        this.latchAccountid = usuario.getLatchAccountid();
        this.latchToken = usuario.getLatchToken();
        this.tokenAuth = usuario.getTokenAuth();
    }

